import React, {useState} from 'react';
import {IconStar, IllustrationThankYou} from './SVGs'
import './styles.css';


export interface CardInterface {
    id?: number;
    view?: string;
    headline: string;
    text: string;
}

const Card = (): React.ReactElement => {
    const [ratingValue, setRatingValue] = useState("");
    const [hasRatingValue, setHasRatingValue] = useState(false)

    const handleChange = (event: React.ChangeEvent<HTMLInputElement>): void => {
        setRatingValue(event.target.value);
    };

    const handleSubmit = (event: React.ChangeEvent<HTMLFormElement>): void => {
        event.preventDefault();
        ratingValue.length > 0 && setHasRatingValue(true);
    };

    return (
        <>
        {!hasRatingValue ? 
            <div className="selection-view card">
                <div className="card-illustration">
                    <IconStar />
                </div>
                <div className="card-content">
                    <h1>How did we do?</h1>
                    <p>Please let us know how we did with your support request. All feedback is appreciated to help us improve our offering.</p>
                </div>
                <div className="card-rating">
                    <form onSubmit={handleSubmit}>
                        <fieldset className="rating-list">
                            <input className="rating-list-item" type="radio" id="eins" name="rating" onChange={handleChange} value="1" />
                            <label>1</label>
                            <input className="rating-list-item" type="radio" id="zwei" name="rating" onChange={handleChange} value="2" />
                            <label>2</label>
                            <input className="rating-list-item" type="radio" id="drei" name="rating" onChange={handleChange} value="3" />
                            <label>3</label>
                            <input className="rating-list-item" type="radio" id="vier" name="rating" onChange={handleChange} value="4" />
                            <label>4</label>
                            <input className="rating-list-item" type="radio" id="fuenf" name="rating" onChange={handleChange} value="5" />
                            <label>5</label>
                        </fieldset>
                        <button className="btn-primary">Search</button>
                    </form>
                </div>
            </div>
        : 
            <div className="result-view card">
                <div className="card-illustration">
                    <IllustrationThankYou />
                </div>
                <div className="card-result">You selected {ratingValue} out of 5</div>
                <div className="card-content">
                    <h1>Thank you!</h1>
                    <p>We appreciate you taking the time to give a rating. If you ever need more support, don't hesitate to get in touch!</p>
                </div>
            </div>
        }
        </>
    );
}

export default Card;
