import React, {useState} from 'react';
import {IconStar} from './SVGs'
import './styles.css';


export interface CardInterface {
    id?: number;
    view?: string;
    headline: string;
    text: string;
}

const headline = "How did we do?";
const text = "Please let us know how we did with your support request. All feedback is appreciated to help us improve our offering.";


const Card = (): React.ReactElement => {
    const [ratingValue, setRatingValue] = useState("");
    const [showRatingValue, setShowRatingValue] = useState(false)

    const handleChange = (event: React.ChangeEvent<HTMLInputElement>): void => {
        setRatingValue(event.target.value);
    };

    const handleSubmit = (event: React.ChangeEvent<HTMLFormElement>): void => {
        event.preventDefault();
        setShowRatingValue(true);
        console.log(ratingValue, showRatingValue);
    };

    return (
        <div className="card">
            <div className="card-illustration">
                <div className="card-illustration-star">
                    <IconStar />
                </div>
            </div>
            <div className="card-content">
              <h1>{headline}</h1>
              <p>{text}</p>
            </div>
            <div className="card-rating">
                <form onSubmit={handleSubmit}>
                    <fieldset>
                        <input type="radio" id="eins" name="rating" onChange={handleChange} value="1" />
                        <label>1</label>
                        <input type="radio" id="zwei" name="rating" onChange={handleChange} value="2" />
                        <label>2</label>
                        <input type="radio" id="drei" name="rating" onChange={handleChange} value="3" />
                        <label>3</label>
                        <input type="radio" id="vier" name="rating" onChange={handleChange} value="4" />
                        <label>4</label>
                        <input type="radio" id="fuenf" name="rating" onChange={handleChange} value="5" />
                        <label>5</label>
                    </fieldset>
                    <button>Search</button>
                </form>
            </div>
        </div>
    );
}

export default Card;
