import React, {useState} from 'react';
import {IconStar, IllustrationThankYou} from './SVGs'
import './styles.css';


export interface CardInterface {
    id?: number;
    view?: string;
    headline: string;
    text: string;
}

const Card = (): React.ReactElement => {
    const [ratingValue, setRatingValue] = useState("");
    const [hasRatingValue, setHasRatingValue] = useState(false);
    const [isSelected, setIsSelected] = useState(false);

    const handleChange = (event: React.ChangeEvent<HTMLInputElement>): void => {
        setRatingValue(event.target.value);
        // setIsSelected(event.target.checked);
    };

    const handleSubmit = (event: React.ChangeEvent<HTMLFormElement>): void => {
        event.preventDefault();
        ratingValue.length > 0 && setHasRatingValue(true);
    };

    return (
        <>
        {!hasRatingValue ? 
            <div className="card">
                <div className="card-icon">
                    <IconStar />
                </div>
                <div className="card-content">
                    <h1>How did we do?</h1>
                    <p>Please let us know how we did with your support request. All feedback is appreciated to help us improve our offering.</p>
                </div>
                <div className="card-rating">
                    <form onSubmit={handleSubmit}>
                        <fieldset className="rating-list">
                            <label className="rating-list-item">
                                <span className={isSelected ? 'selected' : ''}>1</span>
                                <input type="radio" id="eins" name="rating" onChange={handleChange} value="1" />
                            </label>
                            <label className="rating-list-item">
                                <span className={isSelected ? 'selected' : ''}>2</span>
                                <input type="radio" id="zwei" name="rating" onChange={handleChange} value="2" />
                            </label>
                            <label className="rating-list-item">
                                <span className={isSelected ? 'selected' : ''}>3</span>
                                <input type="radio" id="drei" name="rating" onChange={handleChange} value="3" />
                            </label>
                            <label className="rating-list-item">
                                <span className={isSelected ? 'selected' : ''}>4</span>
                                <input type="radio" id="vier" name="rating" onChange={handleChange} value="4" />
                            </label>
                            <label className="rating-list-item">
                                <span className={isSelected ? 'selected' : ''}>5</span>
                                <input type="radio" id="fuenf" name="rating" onChange={handleChange} value="5" />
                            </label>
                        </fieldset>
                        <button className="btn-primary">Submit</button>
                    </form>
                </div>
            </div>
        : 
            <div className="result-view card">
                <IllustrationThankYou />
                <div className="card-result">You selected {ratingValue} out of 5</div>
                <div className="card-content">
                    <h1>Thank you!</h1>
                    <p>We appreciate you taking the time to give a rating. If you ever need more support, don't hesitate to get in touch!</p>
                </div>
            </div>
        }
        </>
    );
}

export default Card;
